import AsyncStorage from "@react-native-async-storage/async-storage"
const getUserPromise= async ()=>{
    let user={}
    await AsyncStorage.getItem("locuser").then((value)=>
    value).then((data)=>user=JSON.parse(data)).catch((error)=>{
       user= {error}
    }
    )
    // console.log(user)
    return user;
}
const getUser =()=>{
    let user={};
    getUserPromise().then((data)=>
    user=data)
    return user

}
export default getUser;
