import { View, Text, Image } from 'react-native';
import { TouchableOpacity } from 'react-native-web';
 const PostItem = (props) => {
  const {item}=props;
  return (
    <View style={{backgroundColor:'yellow', borderWidth:10, borderColor:'cyan',marginTop: 4 ,marginBottom:4,padding:10,borderRadius:20,}}>
      <Text style={{ fontSize: 20, color: 'blue',paddingBottom:16 }}>
        {item?.use}
      </Text>
      <Text style={{ fontSize: 15 ,paddingBottom:16 }}>
        {item?.post}
      </Text>
      <Image source={{uri:"https://raw.githubusercontent.com/riteshp112/Responsive-Resume/master/assets/img/logo.png"}} style={{maxHeight:400}}>
      </Image>
      <View style={{flexDirection:'row',flex:1}}>
        <TouchableOpacity></TouchableOpacity>
      <Text style={{flex:1}}>Likes {item?.likes}</Text> 
     <Text style={{flex:1}}>Comments {item?.comments.length}</Text>
      <Text style={{flex:1}}>Dislikes {item?.dislikes}</Text>
       </View>
    </View>

  )
}
export default PostItem;
