import { View, Image, TextInput, Text, Button, Modal } from "react-native";
import { useState } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage"
import { TouchableOpacity } from "react-native";
import { fetchURL } from "../config";
import Home from "./Home";
let userDetails={};
const setlogloc = async userDetails => {
  await AsyncStorage.setItem("locuser", userDetails);
};
const Login = props => {
  const [modalVisible, setModalVisible] = useState(false);
  const [username, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [loggedIn,setLoggedIn] =useState(false)
  const loginPressed = () => {
    fetch(fetchURL, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({type:'select',table:'testcol',condition:{username, password },limit:1})   
    }).then(
      (response)=>{response.text()}
    ).then((json)=>
      {console.log(json)}
    )
    // .then(
    //   response => {
    //       setModalVisible(false);
    //       userDetails = response?.headers?.map?.output || {}
    //       console.log(response.json(),userDetails)
    //       if (JSON.stringify(userDetails) != "{}") {            
    //         setlogloc(userDetails);
    //         props.logcall(true);
    //         setLoggedIn(true)
    //       } else {
    //         alert("Incorrect username or Password");
    //       }
    //     },
    //     err => {
    //       console.debug("Incorrect username or password xxxxxxxxxxx Server Error");
    //     }
    //   )
      .catch(error => {
        console.debug("Incorrect Username or password", error);
        return error;
      });
  };
  if(loggedIn)
  return(<Home></Home>)
  return (
    <View>
      <TextInput
        onChangeText={value => setUserName(value)}
        placeholder={"Username or Email"}
        style={{ borderWidth: 2 }}
      ></TextInput>
      <TextInput
        onChangeText={value => setPassword(value)}
        placeholder={"Password"}
        secureTextEntry={true}
        style={{ borderWidth: 2, marginTop: 2 }}
      ></TextInput>
      <TouchableOpacity
        onPress={() => {
          alert("Send an e-mail at riteshp112@gmail.com to recover your account.");
        }}
      >
        <Text> Forget Password?</Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          props.signup(true);
        }}
      >
        <Text> Sign Up</Text>
      </TouchableOpacity>
      <Button
        title={"Sign In"}
        onPress={() => {
          setModalVisible(true);
          loginPressed();
        }}
      ></Button>
      <Modal animationType="slide" transparent={true} visible={modalVisible} style={{}}>
        <Text
          style={{
            margin: 20,
            backgroundColor: "white",
            borderRadius: 20,
            padding: 35,
            top: 200,
            color: "blue",
            textAlign: "center",
            alignItems: "center",
            shadowColor: "#000",
            fontSize: 45,
            shadowOffset: {
              width: 0,
              height: 2
            }
          }}
        >
          {" "}
          Please Wait{" "}
        </Text>
      </Modal>
    </View>
  );
};
export { Login, userDetails };
