import React, { useState } from 'react';
import { StyleSheet, Text, TextInput, View, Picker, Button } from 'react-native';
import { fetchURL } from '../config';
const SignUp = (props) => {
  const [name, setName] = useState("");
  const [username, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [gender, setGender] = useState("");
  const [dob, setdob] = useState("");
  function saveUser() {
    alert(JSON.stringify({ name, username, password, gender, dob }));
    fetch(fetchURL, {
      method: 'POST',
      headers: {
        Accept: 'text/plain',
        'Content-Type': 'text/plain'
      },
      body: JSON.stringify({ type: 'insert', table: 'testcol', data: { name, username, password, gender, dob } })
    }).then((response) => {
      const statusCode = response.status;
      if (statusCode == 200) {
        alert("Sign UP sucessful")
        props.signsuccess(false)
      } else {
        "Error Occured"
        setSignedup(false)
      }
    }, (err) => {
      console.debug('error', err)
    })
      .catch((error) => {
        console.debug(error);
        return error;
      });

  }
  return (
    <View>
      <Text>Name:</Text>
      <TextInput style={{ borderWidth: 2, marginTop: 2, height: 25 }} onChangeText={(value) => setName(value)}>
      </TextInput>
      <Text>Username</Text>
      <TextInput style={{ borderWidth: 2, marginTop: 2, height: 25 }} onChangeText={(value) => setUserName(value)}>
      </TextInput>
      <Text>Passworrd</Text>
      <TextInput style={{ borderWidth: 2, marginTop: 2, height: 25 }} onChangeText={(value) => setPassword(value)}>
      </TextInput>
      <Text>Gender</Text>
      <Picker onValueChange={(itemValue, itemIndex) => setGender(itemValue)} >
        <Picker.Item value="" label="Select Your Gender"></Picker.Item>
        <Picker.Item value="M" label="M"></Picker.Item>
        <Picker.Item value="F" label="F"></Picker.Item>
      </Picker >
      <Text>Dob</Text>
      <TextInput style={{ borderWidth: 2, marginTop: 2, height: 25 }} onChangeText={(value)=>setdob(value)} placeholder={"Enter Your Dob"} ></TextInput>
      <Button title={"Sign UP"} onPress={saveUser}></Button>
      <View style={{flexDirection:'row',marginTop:16,justifyContent:'center'}}>
        <Text style={{marginTop:10}}>Already have a Account? </Text>
        <Button title={'Login'} onPress={()=>props.signsuccess(false)}></Button>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
 export default SignUp;
