import { useState } from "react";
import { View, Modal, Text } from "react-native"
import { ScrollView } from "react-native";
//import PostItem from "./PostItem"
import PostItem from "../Components/PostItem";
import { fetchURL } from "../config";
function ObjectId(str) {
  return str;
}
function homegen() {


}
const isCloseToBottom = ({ layoutMeasurement, contentOffset, contentSize }) => {
  const paddingToBottom = 20;
  return layoutMeasurement.height + contentOffset.y >=
    contentSize.height - 50;
};

const loadPost = (setPostItems, setModalVisible, PostItems) => {
  fetch(fetchURL, {
    method: 'POST',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json'
      },
    body: JSON.stringify({type:'select',table:'post',condition:{},limit:5})
  }).then((response) => {
    var a = response.headers.get("output")
    a = a.replace(/\'/g, '"')
    // alert(a)
    a = JSON.parse(a)
    var len = Object.keys(a).length
    //var st=len;
    var psts = []
    for (var i = 0; i < len; i++)
      psts.push([<PostItem key={i} use={a[i].use} post={a[i].post}></PostItem>])
    setPostItems(psts)
    setModalVisible(false)
  }, (err) => {
    console.debug('Something went wrong', err)
  })
    .catch((error) => {
      console.debug("Something went wrong", error);
      return error;
    });
}
const Post = () => {
  const [PostItems, setPostItems] = useState([])
  const [modalVisible, setModalVisible] = useState(true);
  const loadPost = () => {
    fetch(fetchURL, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({type:'select',table:'post',condition:{} ,limit: "" + (PostItems.length+5)})
    }).then((response) => {
      return response.json()}).then((json1)=>{
      var a = json1.response;
      // alert(a[2])
      // a = JSON.parse(a)
      console.log(json1.response)
      var len = a.length
      //var st=len;
      var psts = []
      if (len > PostItems.length)
        for (var i = 0; i < len; i++)
{        let item=a[i]
          item=JSON.parse(item)
          psts.push([<PostItem key={i} item={item}></PostItem>])
}      if (len > PostItems.length)
        setPostItems(psts)
      setModalVisible(false)
    }, (err) => {
      console.debug('Something went wrong', err)
    })
      .catch((error) => {
        console.debug("Something went wrong", error);
        return error;
      });
  }
  if (PostItems.length == 0)
    loadPost(setPostItems, setModalVisible, PostItems);
  return (
    <ScrollView onScroll={({ nativeEvent }) => {
      if (isCloseToBottom(nativeEvent)) {
        loadPost(setPostItems, setModalVisible, PostItems)
        //setModalVisible(!modalVisible)
      }
    }} style={{flexGrow:1}}>
      {PostItems}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        style={{ position: "absolute" }}
      >
        <Text style={{
          margin: 20,
          backgroundColor: "white",
          borderRadius: 20,
          padding: 35,
          top: 200,
          color: 'blue',
          textAlign: 'center',
          alignItems: "center",
          shadowColor: "#000",
          fontSize: 45,
          shadowOffset: {
            width: 0,
            height: 2
          }
        }}> Loading</Text>
      </Modal>
    </ScrollView>

  )
}
export default Post;
